package com.example.backend;import org.springframework.boot.SpringApplication;import org.springframework.boot.autoconfigure.SpringBootApplication;import org.springframework.http.CacheControl;import org.springframework.web.servlet.config.annotation.EnableWebMvc;import org.springframework.web.servlet.config.annotation.ResourceHandlerRegistry;import org.springframework.web.servlet.config.annotation.WebMvcConfigurer;import java.util.concurrent.TimeUnit;@SpringBootApplicationpublic class MobileBackEndApplication implements WebMvcConfigurer {	@Override	public void addResourceHandlers(final ResourceHandlerRegistry registry) {		registry.addResourceHandler("/**")				.addResourceLocations("classpath:/static/","classpath:/image/").				setCacheControl(CacheControl.noCache()).resourceChain(false);	}	public static void main(String[] args) {		SpringApplication.run(MobileBackEndApplication.class, args);	}}